# Georgian translations for common words (basic mapping)
GEORGIAN_TRANSLATIONS = {}

def get_category_theme(filename):
    """Map filename to a word bank theme."""
    name = filename.replace('.json', '').lower()
    if any(x in name for x in ['academic', 'ielts', 'education', 'university', 'school']):
        return 'academic'
    if any(x in name for x in ['animal', 'pet', 'ocean', 'marine']):
        return 'animal'
    if any(x in name for x in ['food', 'drink', 'cook', 'kitchen', 'restaurant', 'cafe', 'supermarket', 'grocery', 'fast-food']):
        return 'food'
    if any(x in name for x in ['sport', 'fitness', 'gym', 'mma', 'game']):
        return 'sport'
    if any(x in name for x in ['tech', 'computer', 'programming', 'internet', 'phone', 'app', 'social-media']):
        return 'technology'
    if any(x in name for x in ['music', 'art', 'movie', 'cinema', 'entertainment', 'youtube']):
        return 'music'
    if any(x in name for x in ['work', 'business', 'job', 'office', 'career', 'startup', 'freelanc', 'email']):
        return 'work'
    if any(x in name for x in ['emotion', 'feeling', 'mood', 'mental', 'psychology', 'personality', 'motivation', 'self-improvement']):
        return 'emotion'
    if any(x in name for x in ['health', 'body', 'medical', 'doctor', 'bathroom', 'hygiene']):
        return 'health'
    if any(x in name for x in ['travel', 'transport', 'airport', 'hotel', 'visa', 'immigration', 'taxi', 'public-transport', 'car', 'driving']):
        return 'travel'
    if any(x in name for x in ['home', 'house', 'housing', 'furniture', 'room', 'household', 'apartment', 'garden']):
        return 'home'
    return None

if __name__ == '__main__':
    if len(sys.argv) < 2:
        print("Usage: python3 expand_category.py file1.json file2.json ...")
        sys.exit(1)
    
    for filename in sys.argv[1:]:
        filepath = os.path.join(os.path.dirname(os.path.abspath(__file__)), filename)
        if not os.path.exists(filepath):
            print(f"Not found: {filename}")
            continue
        
        with open(filepath, 'r') as f:
            cards = json.load(f)
        
        existing_words = {c['english'].lower() for c in cards}
        current_count = len(cards)
        
        if current_count >= 200:
            print(f"{filename}: already has {current_count} words, skipping")
            continue
        
        needed = 200 - current_count
        theme = get_category_theme(filename)
        
        if not theme:
            print(f"{filename}: no theme mapping, skipping (has {current_count})")
            continue
        
        word_bank = WORD_BANKS.get(theme, [])
        # Filter out already existing words
        new_words = [w for w in word_bank if w.lower() not in existing_words and ' ' not in w]
        
        added = 0
        category = cards[0].get('category', filename.replace('.json', '')) if cards else filename.replace('.json', '')
        
        for word in new_words[:needed]:
            cards.append({
                "english": word,
                "georgian": "",
                "pronunciation": "",
                "example_en": "",
                "example_ka": "",
                "category": category
            })
            added += 1
        
        with open(filepath, 'w') as f:
            json.dump(cards, f, ensure_ascii=False, indent=2)
        
        print(f"{filename}: {current_count} â†’ {len(cards)} (+{added})")
